var reqStr=current.variables.requestor;
var openedby=current.openedBy;
var company=current.variables.company;
var manager=current.variables.manager;
var priority=current.variables.priority;
var short_desc=current.variables.short_description;


var gr= new GlideRecord("x_1583821_itm_asset_request");
gr.initialize();
gr.u_reference_1=reqStr;
gr.opened_by=openedby;
gr.company=company;
gr.u_choice_prio=priority;
gr.u_string_shd=short_desc;
gr.u_choice_t_asset=current.variables.asset_type;
gr.u_choice_consu=current.variables.consumable;
current.variables.it_asset=gr.insert();

