var req=current.variables.it_asset;
	var gr = new GlideRecord("x_1583821_itm_asset_request");
	gr.addQuery("sys_id",req);
	gr.setLimit(1);
	gr.query();
	if(gr.next()){
		gr.u_choice_status="closedIncomplete";
		gr.update();
		var asset=gr.asset;
		var grCmdb=new GlideRecord("cmdb_ci");
		grCmdb.addQuery("sys_id",asset);
		grCmdb.query();
		if(grCmdb.next()){
			var hardware=new GlideRecord("alm_hardware");
			hardware.addQuery("ci.sys_id",asset);
			hardware.query();

			if(hardware.next()){
				var  hardId=hardware.sys_id;
				var consumable=new GlideRecord("alm_consumable");
				consumable.addQuery("parent.sys_id",hardId);
				consumable.query();

				if(consumable.next()){
					var mod=consumable.model;
					var model=new GlideRecord("cmdb_model");
					model.addQuery("sys_id",mod);
					model.query();
					if(model.next()){
						model.deleteRecord();
					}
					consumable.deleteRecord();
				}
				hardware.deleteRecord();
			}
			grCmdb.deleteRecord();
		}

		
	}

